import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Sprout, Droplets, Scissors, Camera, Plus, Calendar, TrendingUp } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import toast from 'react-hot-toast';

import { plantsApi, logsApi } from '../utils/api';

const Dashboard = () => {
  const [plants, setPlants] = useState([]);
  const [recentLogs, setRecentLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalPlants: 0,
    activeStages: {},
    recentActivity: 0,
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [plantsData, logsData] = await Promise.all([
        plantsApi.getAll(),
        logsApi.getAll({ limit: 10 })
      ]);

      setPlants(plantsData);
      setRecentLogs(logsData);

      // Calculate stats
      const stageCount = plantsData.reduce((acc, plant) => {
        acc[plant.stage] = (acc[plant.stage] || 0) + 1;
        return acc;
      }, {});

      setStats({
        totalPlants: plantsData.length,
        activeStages: stageCount,
        recentActivity: logsData.length,
      });
    } catch (error) {
      toast.error('Failed to load dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStageIcon = (stage) => {
    switch (stage) {
      case 'seedling': return 'ðŸŒ±';
      case 'vegetative': return 'ðŸŒ¿';
      case 'flowering': return 'ðŸŒ¸';
      case 'harvest': return 'ðŸŒ¾';
      case 'cured': return 'ðŸ“¦';
      default: return 'ðŸŒ±';
    }
  };

  const getLogIcon = (type) => {
    switch (type) {
      case 'watering': return <Droplets className="w-4 h-4 text-blue-500" />;
      case 'feeding': return <TrendingUp className="w-4 h-4 text-green-500" />;
      case 'pruning': return <Scissors className="w-4 h-4 text-orange-500" />;
      case 'photo': return <Camera className="w-4 h-4 text-purple-500" />;
      default: return <Calendar className="w-4 h-4 text-gray-500" />;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="dashboard-header">
