import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Sprout, BarChart3, FileText, Home, Thermometer } from 'lucide-react';

const Header = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Dashboard', icon: Home },
    { path: '/plants', label: 'Plants', icon: Sprout },
    { path: '/logs', label: 'Activity Log', icon: FileText },
    { path: '/environment', label: 'Environment', icon: Thermometer },
  ];

  return (
    <header style={{ background: 'var(--surface)', borderBottom: '1px solid var(--border)' }} className="shadow-lg">
      <div className="container mx-auto">
        <div className="flex items-center justify-between py-4">
          <Link to="/" className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-r from-green-400 to-green-600 rounded-lg flex items-center justify-center">
              <Sprout className="w-6 h-6 text-white" />
            </div>
            <div>
              <h1 className="text-2xl font-bold" style={{ color: 'var(--text-primary)' }}>GrowLogger</h1>
              <p className="text-sm" style={{ color: 'var(--text-secondary)' }}>Cannabis Cultivation Tracker</p>
            </div>
          </Link>

          <nav className="flex items-center gap-1">
            {navItems.map(({ path, label, icon: Icon }) => (
              <Link
                key={path}
                to={path}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-all ${
                  location.pathname === path
                    ? 'font-semibold'
                    : 'hover:bg-opacity-80'
                }`}
                style={{
                  background: location.pathname === path ? 'rgba(74, 222, 128, 0.2)' : 'transparent',
                  color: location.pathname === path ? '#4ade80' : 'var(--text-secondary)',
                }}
                onMouseEnter={(e) => {
                  if (location.pathname !== path) {
                    e.target.style.background = 'var(--surface-hover)';
                    e.target.style.color = 'var(--text-primary)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (location.pathname !== path) {
                    e.target.style.background = 'transparent';
                    e.target.style.color = 'var(--text-secondary)';
                  }
                }}
              >
                <Icon className="w-4 h-4" />
                <span className="hidden sm:inline">{label}</span>
              </Link>
            ))}
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
