import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { ArrowLeft, Calendar, Plus, Edit, Home, Save, X } from 'lucide-react';
import { formatDistanceToNow, format } from 'date-fns';
import { useForm } from 'react-hook-form';
import toast from 'react-hot-toast';

import { plantsApi, logsApi } from '../utils/api';

const PlantDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [plant, setPlant] = useState(null);
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [growTents, setGrowTents] = useState([]);

  const { register, handleSubmit, reset, formState: { errors, isSubmitting } } = useForm();

  useEffect(() => {
    fetchPlantData();
    fetchGrowTents();
  }, [id]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchPlantData = async () => {
    try {
      setLoading(true);
      const [plantData, logsData] = await Promise.all([
        plantsApi.getById(id),
        logsApi.getAll({ plant_id: id })
      ]);
      setPlant(plantData);
      setLogs(logsData);
    } catch (error) {
      toast.error('Failed to load plant data');
      console.error('Plant detail error:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStageIcon = (stage) => {
    switch (stage) {
      case 'seedling': return 'ðŸŒ±';
      case 'vegetative': return 'ðŸŒ¿';
      case 'flowering': return 'ðŸŒ¸';
      case 'harvest': return 'ðŸŒ¾';
      case 'cured': return 'ðŸ“¦';
      default: return 'ðŸŒ±';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading"></div>
      </div>
    );
  }

  if (!plant) {
    return (
      <div className="text-center py-12">
        <h2 className="text-xl font-semibold text-gray-600">Plant not found</h2>
        <Link to="/plants" className="btn btn-primary mt-4">
          <ArrowLeft className="w-4 h-4" />
          Back to Plants
        </Link>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Link to="/plants" className="btn btn-secondary">
          <ArrowLeft className="w-4 h-4" />
          Back
        </Link>
        <div>
          <h1 className="text-3xl font-bold text-gray-800">{plant.name}</h1>
          <p className="text-gray-600">{plant.strain || 'Unknown strain'}</p>
        </div>
        <span className={`stage-badge stage-${plant.stage}`}>
          {getStageIcon(plant.stage)} {plant.stage}
        </span>
      </div>

      {/* Plant Info Card */}
      <div className="card">
        <h2 className="text-xl font-semibold mb-4">Plant Information</h2>
        <div className="grid grid-2 gap-4">
          {plant.planted_date && (
            <div>
              <label className="text-sm font-medium text-gray-600">Planted Date</label>
              <p className="text-gray-800">{format(new Date(plant.planted_date), 'PPP')}</p>
              <p className="text-sm text-gray-500">{formatDistanceToNow(new Date(plant.planted_date))} ago</p>
            </div>
          )}
          {plant.expected_harvest && (
            <div>
              <label className="text-sm font-medium text-gray-600">Expected Harvest</label>
              <p className="text-gray-800">{format(new Date(plant.expected_harvest), 'PPP')}</p>
            </div>
          )}
          <div>
            <label className="text-sm font-medium text-gray-600">Total Logs</label>
            <p className="text-gray-800">{plant.log_count || 0} activities recorded</p>
          </div>
          <div>
            <label className="text-sm font-medium text-gray-600">Last Activity</label>
            <p className="text-gray-800">
              {plant.last_log_date 
                ? formatDistanceToNow(new Date(plant.last_log_date)) + ' ago'
                : 'No activities yet'
              }
            </p>
          </div>
        </div>
        {plant.notes && (
          <div className="mt-4">
            <label className="text-sm font-medium text-gray-600">Notes</label>
            <p className="text-gray-800 mt-1">{plant.notes}</p>
          </div>
        )}
      </div>

      {/* Recent Logs */}
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-semibold">Recent Activity</h2>
          <Link to="/logs" className="btn btn-primary btn-sm">
            <Plus className="w-4 h-4" />
            Add Log
          </Link>
        </div>

        {logs.length === 0 ? (
          <div className="text-center py-8">
            <Calendar className="w-12 h-12 text-gray-300 mx-auto mb-4" />
            <p className="text-gray-500 mb-4">No activity logs yet</p>
            <Link to="/logs" className="btn btn-primary">
              <Plus className="w-4 h-4" />
              Add First Log
            </Link>
          </div>
        ) : (
          <div className="space-y-3">
            {logs.slice(0, 10).map((log) => (
              <div key={log.id} className="flex items-start gap-3 p-3 bg-gray-50 rounded-lg">
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <span className="font-medium text-gray-800 capitalize">{log.type}</span>
                    <span className="text-sm text-gray-500">â€¢</span>
                    <span className="text-sm text-gray-500">
                      {formatDistanceToNow(new Date(log.logged_at))} ago
                    </span>
                  </div>
                  {log.description && (
                    <p className="text-sm text-gray-600">{log.description}</p>
                  )}
                  {log.value && (
                    <p className="text-sm text-gray-500">
                      Value: {log.value} {log.unit || ''}
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PlantDetail;
