import React, { useState, useEffect } from 'react';
import { Plus, Thermometer, Droplets, TestTube, Sun, Trash2, TrendingUp, Camera } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { format, formatDistanceToNow } from 'date-fns';
import toast from 'react-hot-toast';

import { environmentApi } from '../utils/api';
import ImageUpload from '../components/ImageUpload';

const Environment = () => {
  const [environmentLogs, setEnvironmentLogs] = useState([]);
  const [weeklyData, setWeeklyData] = useState([]);
  const [latestReading, setLatestReading] = useState({});
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [showWeekly, setShowWeekly] = useState(false);

  const { register, handleSubmit, reset, formState: { errors, isSubmitting } } = useForm();

  useEffect(() => {
    fetchEnvironmentData();
    fetchLatestReading();
    fetchWeeklyData();
  }, []);

  const fetchEnvironmentData = async () => {
    try {
      setLoading(true);
      const data = await environmentApi.getAll({ limit: 50 });
      setEnvironmentLogs(data);
    } catch (error) {
      toast.error('Failed to load environment data');
      console.error('Environment fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchLatestReading = async () => {
    try {
      const data = await environmentApi.getLatest();
      setLatestReading(data);
    } catch (error) {
      console.error('Latest reading fetch error:', error);
    }
  };

  const fetchWeeklyData = async () => {
    try {
      const data = await environmentApi.getWeekly({ weeks: 8 });
      setWeeklyData(data);
    } catch (error) {
      console.error('Weekly data fetch error:', error);
    }
  };

  const onSubmit = async (data) => {
    try {
      // Convert form data to proper types
      const environmentData = {
        ...data,
        temperature: data.temperature ? parseFloat(data.temperature) : null,
        humidity: data.humidity ? parseFloat(data.humidity) : null,
        ph_level: data.ph_level ? parseFloat(data.ph_level) : null,
        light_hours: data.light_hours ? parseFloat(data.light_hours) : null,
        logged_at: data.logged_at || new Date().toISOString(),
      };

      await environmentApi.create(environmentData);
      toast.success('Environment data added successfully');

      fetchEnvironmentData();
      fetchLatestReading();
      fetchWeeklyData();
      resetForm();
    } catch (error) {
      toast.error(error.message);
    }
  };

  const handleDelete = async (logId) => {
    if (window.confirm('Are you sure you want to delete this environment log?')) {
      try {
        await environmentApi.delete(logId);
        toast.success('Environment log deleted successfully');
        fetchEnvironmentData();
        fetchLatestReading();
        fetchWeeklyData();
      } catch (error) {
        toast.error('Failed to delete environment log');
      }
    }
  };

  const resetForm = () => {
    setShowForm(false);
    reset();
  };

  const getCurrentDateTime = () => {
    const now = new Date();
    return format(now, "yyyy-MM-dd'T'HH:mm");
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="loading"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Environment</h1>
          <p className="text-gray-600 mt-1">Track temperature, humidity, pH, and lighting conditions</p>
        </div>
        <div className="flex gap-3">
          <button
            onClick={() => setShowWeekly(!showWeekly)}
            className={`btn ${showWeekly ? 'btn-secondary' : 'btn-outline'}`}
          >
            <TrendingUp className="w-4 h-4" />
            {showWeekly ? 'Show Logs' : 'Weekly Stats'}
          </button>
          <button
            onClick={() => setShowForm(true)}
            className="btn btn-primary"
          >
            <Plus className="w-4 h-4" />
            Add Reading
          </button>
        </div>
      </div>

      {/* Latest Reading Cards */}
      {latestReading.id && (
        <div className="grid grid-4">
          <div className="env-card">
            <Thermometer className="w-8 h-8 text-red-400 mx-auto mb-2" />
            <div className="env-value">
              {latestReading.temperature ? `${latestReading.temperature}Â°F` : 'N/A'}
            </div>
            <div className="env-label">Temperature</div>
          </div>
          <div className="env-card">
            <Droplets className="w-8 h-8 text-blue-400 mx-auto mb-2" />
            <div className="env-value">
              {latestReading.humidity ? `${latestReading.humidity}%` : 'N/A'}
            </div>
            <div className="env-label">Humidity</div>
          </div>
          <div className="env-card">
            <TestTube className="w-8 h-8 text-green-400 mx-auto mb-2" />
            <div className="env-value">
              {latestReading.ph_level ? latestReading.ph_level.toFixed(1) : 'N/A'}
            </div>
            <div className="env-label">pH Level</div>
          </div>
          <div className="env-card">
            <Sun className="w-8 h-8 text-yellow-400 mx-auto mb-2" />
            <div className="env-value">
              {latestReading.light_hours ? `${latestReading.light_hours}h` : 'N/A'}
            </div>
            <div className="env-label">Light Hours</div>
          </div>
        </div>
      )}

      {/* Add Form */}
      {showForm && (
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold">Add Environment Reading</h2>
            <button
              onClick={resetForm}
              className="btn btn-secondary"
            >
              Cancel
            </button>
          </div>

          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-2">
              <div className="form-group">
                <label className="label">Temperature (Â°F)</label>
                <input
                  type="number"
                  step="0.1"
                  className="input"
                  {...register('temperature', { 
                    min: { value: -50, message: 'Temperature must be above -50Â°F' },
                    max: { value: 150, message: 'Temperature must be below 150Â°F' }
                  })}
                  placeholder="e.g., 75.5"
                />
                {errors.temperature && <p className="text-error text-sm mt-1">{errors.temperature.message}</p>}
              </div>

              <div className="form-group">
                <label className="label">Humidity (%)</label>
                <input
                  type="number"
                  step="0.1"
                  className="input"
                  {...register('humidity', { 
                    min: { value: 0, message: 'Humidity must be 0% or higher' },
                    max: { value: 100, message: 'Humidity cannot exceed 100%' }
                  })}
                  placeholder="e.g., 65.0"
                />
                {errors.humidity && <p className="text-error text-sm mt-1">{errors.humidity.message}</p>}
              </div>

              <div className="form-group">
                <label className="label">pH Level</label>
                <input
                  type="number"
                  step="0.1"
                  className="input"
                  {...register('ph_level', { 
                    min: { value: 0, message: 'pH must be 0 or higher' },
                    max: { value: 14, message: 'pH cannot exceed 14' }
                  })}
                  placeholder="e.g., 6.5"
                />
                {errors.ph_level && <p className="text-error text-sm mt-1">{errors.ph_level.message}</p>}
              </div>

              <div className="form-group">
                <label className="label">Light Hours</label>
                <input
                  type="number"
                  step="0.1"
                  className="input"
                  {...register('light_hours', { 
                    min: { value: 0, message: 'Light hours must be 0 or higher' },
                    max: { value: 24, message: 'Light hours cannot exceed 24' }
                  })}
                  placeholder="e.g., 18.0"
                />
                {errors.light_hours && <p className="text-error text-sm mt-1">{errors.light_hours.message}</p>}
              </div>

              <div className="form-group">
                <label className="label">Date & Time</label>
                <input
                  type="datetime-local"
                  className="input"
                  {...register('logged_at')}
                  defaultValue={getCurrentDateTime()}
                />
              </div>
            </div>

            <div className="form-group">
              <label className="label">Notes</label>
              <textarea
                className="input textarea"
                {...register('notes')}
                placeholder="Any observations about environmental conditions..."
                rows={3}
              />
            </div>

            <div className="flex gap-3">
              <button
                type="submit"
                disabled={isSubmitting}
                className="btn btn-primary"
              >
                {isSubmitting && <div className="loading"></div>}
                Add Reading
              </button>
              <button
                type="button"
                onClick={resetForm}
                className="btn btn-secondary"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Weekly Stats View */}
      {showWeekly ? (
        <div className="card">
          <h2 className="text-xl font-semibold mb-4">Weekly Averages</h2>
          {weeklyData.length === 0 ? (
            <p className="text-gray-500 text-center py-8">No weekly data available</p>
          ) : (
            <div className="space-y-4">
              {weeklyData.map((week, index) => (
                <div key={week.week} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-3">
                    <h3 className="font-semibold">
                      Week of {format(new Date(week.week_start), 'MMM dd, yyyy')}
                    </h3>
                    <span className="text-sm text-gray-500">
                      {week.reading_count} readings
                    </span>
                  </div>
                  <div className="grid grid-4 gap-4 text-sm">
                    <div className="text-center">
                      <div className="font-medium text-red-600">
                        {week.avg_temperature ? `${week.avg_temperature.toFixed(1)}Â°F` : 'N/A'}
                      </div>
                      <div className="text-gray-500">Avg Temp</div>
                    </div>
                    <div className="text-center">
                      <div className="font-medium text-blue-600">
                        {week.avg_humidity ? `${week.avg_humidity.toFixed(1)}%` : 'N/A'}
                      </div>
                      <div className="text-gray-500">Avg Humidity</div>
                    </div>
                    <div className="text-center">
                      <div className="font-medium text-green-600">
                        {week.avg_ph_level ? week.avg_ph_level.toFixed(1) : 'N/A'}
                      </div>
                      <div className="text-gray-500">Avg pH</div>
                    </div>
                    <div className="text-center">
                      <div className="font-medium text-yellow-600">
                        {week.avg_light_hours ? `${week.avg_light_hours.toFixed(1)}h` : 'N/A'}
                      </div>
                      <div className="text-gray-500">Avg Light</div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      ) : (
        /* Environment Logs */
        <div className="card">
          <h2 className="text-xl font-semibold mb-4">Recent Readings</h2>
          {environmentLogs.length === 0 ? (
            <div className="text-center py-12">
              <Thermometer className="w-16 h-16 text-gray-300 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-600 mb-2">No environment data yet</h3>
              <p className="text-gray-500 mb-4">Start tracking your grow environment conditions</p>
              <button
                onClick={() => setShowForm(true)}
                className="btn btn-primary"
              >
                <Plus className="w-4 h-4" />
                Add First Reading
              </button>
            </div>
          ) : (
            <div className="space-y-4">
              {environmentLogs.map((log) => (
                <div key={log.id} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-4 mb-2">
                        <span className="text-sm text-gray-500">
                          {formatDistanceToNow(new Date(log.logged_at))} ago
                        </span>
                        <span className="text-xs text-gray-400">
                          {format(new Date(log.logged_at), 'MMM dd, yyyy HH:mm')}
                        </span>
                      </div>

                      <div className="grid grid-4 gap-4 mb-3">
                        <div className="flex items-center gap-2">
                          <Thermometer className="w-4 h-4 text-red-500" />
                          <span className="text-sm">
                            {log.temperature ? `${log.temperature}Â°F` : 'N/A'}
                          </span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Droplets className="w-4 h-4 text-blue-500" />
                          <span className="text-sm">
                            {log.humidity ? `${log.humidity}%` : 'N/A'}
                          </span>
                        </div>
                        <div className="flex items-center gap-2">
                          <TestTube className="w-4 h-4 text-green-500" />
                          <span className="text-sm">
                            {log.ph_level ? `pH ${log.ph_level}` : 'N/A'}
                          </span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Sun className="w-4 h-4 text-yellow-500" />
                          <span className="text-sm">
                            {log.light_hours ? `${log.light_hours}h` : 'N/A'}
                          </span>
                        </div>
                      </div>

                      {log.notes && (
                        <p className="text-gray-600 text-sm">{log.notes}</p>
                      )}
                    </div>

                    <button
                      onClick={() => handleDelete(log.id)}
                      className="btn btn-danger btn-sm"
                      title="Delete Reading"
                    >
                      <Trash2 className="w-3 h-3" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Spider Farmer Integration Info */}
      <div className="card bg-blue-50 border-blue-200">
        <h3 className="text-lg font-semibold text-blue-900 mb-2">Spider Farmer GGS Integration</h3>
        <p className="text-blue-800 text-sm mb-3">
          To automatically sync data from your Spider Farmer GGS controller, configure it to send HTTP POST requests to:
        </p>
        <code className="block bg-blue-100 text-blue-900 p-2 rounded text-sm font-mono mb-3">
          POST {window.location.origin.replace('3000', '5000')}/api/environment/spider-farmer
        </code>
        <p className="text-blue-800 text-sm">
          The endpoint accepts: temperature, humidity, ph, light_duration (seconds), vpd, and timestamp.
        </p>
      </div>
    </div>
  );
};

export default Environment;
